from Crypto.Util.number import *
import os

p = getPrime(1024)
q = getPrime(1024)
N = p * q
e = 3

#assert len(flag) == 96
flag=open("dist/flag.txt","r").readline().strip().encode()
padding = os.urandom(25)
m = bytes_to_long(flag * 2)
m_ = bytes_to_long((flag + padding) * 2)
c1 = pow(m, e, N)
c2 = pow(m_, e, N)

print('N =', N)
print('c1 =', c1)
print('c2 =', c2)
print('padding =',padding)

